import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';
import TourCard from '../shared/TourCard';
import SearchBar from '../shared/SearchBar';
import Newsletter from '../shared/Newsletter';
import { Container, Row, Col } from "reactstrap";
import tourData from '../assets/data/tours'; // Temp local data

import "../styles/tour.css";

const Tours = () => {
  const [tours, setTours] = useState([]);        // ✅ for future dynamic data
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  useEffect(() => {
    // Simulate backend call for now
    setTours(tourData);

    const totalTours = tourData.length;         // In future: fetched count
    const pages = Math.ceil(totalTours / 4);
    setPageCount(pages);
  }, [page]);

  return (
    <>
      <CommonSection title="All Tours" />

      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>

      <section className="pt-0">
        <Container>
          <Row>
            {tours.slice(page * 4, page * 4 + 4).map((tour) => (
              <Col lg="3" className="mb-4" key={tour.id}>
                <TourCard tour={tour} />
              </Col>
            ))}

            <Col lg="12">
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map((number) => (
                  <span
                    key={number}
                    onClick={() => setPage(number)}
                    className={page === number ? "active__page" : ""}
                  >
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
        </Container>
      </section>

      <Newsletter />
    </>
  );
};

export default Tours;
