import React, { useState, useEffect } from 'react';
import '../styles/tour-details.css';
import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';
import calculateAvgRating from './../utils/avgRating';
import avatar from '../assets/images/avatar.jpg';
import Booking from '../components/Booking/Booking';
import Newsletter from '../shared/Newsletter';

const TourDetails = () => {
  const { id } = useParams();
  const [tourRating, setTourRating] = useState(null);
  const [reviewText, setReviewText] = useState('');
  const [tour, setTour] = useState(null);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const foundTour = tourData.find(t => t.id === id);
    if (foundTour) {
      setTour(foundTour);
      setReviews(foundTour.reviews || []);
    }
  }, [id]);

  if (!tour) {
    return (
      <Container className="text-center mt-5">
        <h4>Tour not found</h4>
      </Container>
    );
  }

  const { photo, title, desc, price, address, city, distance, maxGroupSize } = tour;
  const { totalRating, avgRating } = calculateAvgRating(reviews);
  const options = { day: 'numeric', month: 'long', year: 'numeric' };

  const submitHandler = async (e) => {
    e.preventDefault();
    if (!tourRating) return alert("Please select a star rating.");

    try {
      const res = await fetch(`/api/review/${id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify({ reviewText, rating: tourRating }),
      });

      const result = await res.json();
      if (result.success) {
        alert("Review submitted successfully!");
        setReviews(prev => [...prev, result.data]);
        setReviewText('');
        setTourRating(null);
      } else {
        alert(result.message || "Failed to submit review");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting review");
    }
  };

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour__content">
                <img src={photo} alt="" />
                <div className="tour__info">
                  <h2>{title}</h2>
                  <div className="d-flex align-items-center gap-5">
                    <span className="tour__rating d-flex align-items-center gap-1">
                      <i className="ri-star-s-fill" style={{ color: "var(--secondary-color)" }}></i>
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? ("Not rated") : (
                        <span>({reviews?.length})</span>
                      )}
                    </span>
                    <span>
                      <i className="ri-map-pin-user-line"></i> {address}
                    </span>
                  </div>
                  <div className="tour__extra-details">
                    <span>
                      <i className="ri-map-pin-2-line"> {city}</i>
                    </span>
                    <span>
                      <i className="ri-money-dollar-circle-line"> {price} / per person</i>
                    </span>
                    <span>
                      <i className="ri-group-line"> {maxGroupSize}</i>
                    </span>
                  </div>
                  <h5>Description</h5>
                  <p>{desc}</p>
                </div>

                {/* Reviews */}
                <div className="tour__reviews mt-4">
                  <h4>Reviews ({reviews?.length})</h4>
                  <Form onSubmit={submitHandler}>
                    <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                      {[1, 2, 3, 4, 5].map((val) => (
                        <span key={val} onClick={() => setTourRating(val)}>
                          {val} <i className="ri-star-s-fill"></i>
                        </span>
                      ))}
                    </div>
                    <div className="review__input">
                      <input
                        type="text"
                        value={reviewText}
                        onChange={(e) => setReviewText(e.target.value)}
                        placeholder="Share your thoughts"
                        required
                      />
                      <button className="btn primary__btn text-white" type="submit">
                        Submit
                      </button>
                    </div>
                  </Form>

                  <ListGroup className="user__reviews mt-3">
                    {reviews?.map((review, index) => (
                      <div className="review__item" key={index}>
                        <img src={avatar} alt="user" />
                        <div className="w-100">
                          <div className="d-flex align-items-center justify-content-between">
                            <div>
                              <h5>{review.username || 'Anonymous'}</h5>
                              <p>{new Date(review.createdAt || Date.now()).toLocaleDateString("en-US", options)}</p>
                            </div>
                            <span className="d-flex align-items-center">
                              {review.rating} <i className="ri-star-s-fill"></i>
                            </span>
                          </div>
                          <h6>{review.reviewText}</h6>
                        </div>
                      </div>
                    ))}
                  </ListGroup>
                </div>
              </div>
            </Col>
            <Col lg="4">
              <Booking tour={tour} avgRating={avgRating} />
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default TourDetails;
